function App() {
  const [books, setbooks] = useState([]);

  const searchbooks = (event) =>{
    const search = async ()=>{
      const res = await BooksApi.search(event.target.value);
      setbooks(res)
    }
  }
    return (
    <div className="app">
        <SearchBooks books={books} onchangeinsearch={searchbooks}/> 
        <ListBooks />
    </div>
  );
}

export default App;
