import "./App.css";
import { useState, useEffect,forceUpa } from "react";
import SearchBooks from "./components/SearchBooks";
import ListBooks from "./components/ListBooks";
import * as BooksApi from "./BooksAPI";
import { Route , Routes } from "react-router-dom";

function App() {

  const [books, setbooks] = useState([]);
  const [inputvalue, setinputvalue] = useState("");
  const [allbooks, setallbooks] = useState([])

  const searchbooks = (event) =>{
    setinputvalue(event.target.value)
    const search = async () =>{
      try{
        if(event.target.value.trim() !== ""){
        const res = await BooksApi.search(event.target.value);
          setbooks(res);
        }else{
          setbooks([]);
        }
      }
      catch(e){
        console.log("error")
      }
    }
    search()
  }

  useEffect(() =>{
    const getall = async () =>{
      const res2 = await BooksApi.getAll();
      setallbooks(res2)
    } 
    getall();
    forceUpdate();
  },[]);

    return (
      <Routes> 
        <Route exact path="/" element={<ListBooks allbooks={allbooks}/>}/>
        <Route path="/search" element={<SearchBooks books={books} onchangeinsearch={searchbooks} inputvalue={inputvalue}/>}/> 
      </Routes>

  );
}

export default App;

