import "./App.css";
import { useState, useEffect } from "react";
import SearchBooks from "./components/SearchBooks";
import ListBooks from "./components/ListBooks";
import * as BooksApi from "./BooksAPI";
import { Route , Routes } from "react-router-dom";

function App() {

  const [books, setbooks] = useState([]);
  const [inputvalue, setinputvalue] = useState("");
  const [bookstate, setbookstate] = useState("none");

  const searchbooks = (event) =>{
    setinputvalue(event.target.value)
    const search = async () =>{
      try{
        if(event.target.value.trim() !== ""){
        const res = await BooksApi.search(event.target.value);
          setbooks(res);
        }else{
          setbooks([]);
        }
      }
      catch(e){
        console.log("error")
      }
    }
    search()
  }

  const updatebook = (event) => {
    books.forEach(book => {
      const update =async () => {
        await BooksApi.update(book,event.target.value);
      }
      update()
    })
    setbookstate(event.target.value);
  };

  useEffect(() =>{

  },[])

    return (
      <Routes> 
        <Route path="/search" element={<SearchBooks books={books} onchangeinsearch={searchbooks} inputvalue={inputvalue}/>}/>
        <Route exact path="/" element={<ListBooks updatebook={updatebook} bookstate={bookstate} />}/>
      </Routes>

  );
}

export default App;

