import {useState} from "react";
import * as BooksApi from "../BooksAPI";

function Book(props) {
    const [bookstate, setbookstate] = useState("None");

    const statevalue = (event)=>{
        const flip = tru
        const update = async () => {
            await BooksApi.update(props.book , event.target.value);
        }
        setbookstate(event.target.value)
        update()
    };
        return(
            <li key={props.book.id}>
                <div className="book">
                    <div className="book-top">
                        <div
                            className="book-cover"
                            style={{
                                 width: 128,
                                height: 193,
                                backgroundImage:
                                `url(${props.book.imageLinks?.smallThumbnail})`,
                            }}
                        ></div>
                        <div className="book-shelf-changer">
                            <select value={bookstate} onChange={statevalue}>
                                <option value="None" disabled>
                                Move to...
                                </option>
                                <option value="currentlyReading">
                                Currently Reading
                                </option>
                                <option value="wantToRead">Want to Read</option>
                                <option value="read">Read</option>
                                <option value="none">None</option>
                            </select>
                        </div>
                    </div>
                    <div className="book-title">{props.book.title}</div>
                    <div className="book-authors">{props.book.authors}</div>
                </div>
            </li>
        )
}

export default Book;
